import React from 'react';
import jsPDF from 'jspdf'
import 'jspdf-autotable'
import DUMMY_DATA from './data';

function ShowData() {

    const columns = [
        { title: "Id", field: "id" },
        { title: "Name", field: "name" },
        { title: "DocumentNumber", field: "documentnumber" },
        { title: "Signature", field: "signature" },
    ]



    const downloadPdf = () => {
        const doc = new jsPDF()
        doc.text("Universidad del bosque", 105, 10, null, null, "center")
        doc.text("Proceso de admisiones pregrado de Medicina", 40, 20)
        doc.text("Segundo Periodo Acamedico de 2019", 20, 30)
        doc.text("Salon I104", 20, 40)
        doc.autoTable({
            startY: 50,
            theme: "grid",

            columns: columns.map((col) => ({ ...col, datakey: col.field })),

            body: DUMMY_DATA
        })
        doc.save('table.pdf')
    }

    return (
        <div className="App">
            <h1 align="center">React-App</h1>
            <h4 align='center'>Export Data to Pdf in Material Table</h4>
            <button onClick={downloadPdf}>Exportar</button>

        </div>
    );
}

export default ShowData;
