import React from 'react';
import jsPDF from 'jspdf'
import 'jspdf-autotable'
import DUMMY_DATA from './data';

function ShowData() {

    const columns = [
        { title: "Id", field: "id" },
        { title: "Name", field: "nombre" },
        { title: "Document Number", field: "documento" },
        { title: "ClassRoom", field: "salon" },
        { title: "Signature", field: "signature" },
    ]
    let id = 1;

    const data = DUMMY_DATA.map(person => [
        'N.'+ id++,
        person.nombre,
        person.documento,
        person.salon
    ])


    const downloadPdf = () => {
        const doc = new jsPDF()
        doc.text("Universidad del bosque", 105, 10, null, null, "center")
        doc.text("Proceso de admisiones pregrado de Medicina", 105, 20, null, null, "center")
        doc.text("Segundo Periodo Acamedico de 2022", 105, 30, null, null, "center")
        doc.text("Salon I104", 105, 40, null, null, "center")
        doc.autoTable({
            startY: 50,
            theme: "grid",

            columns: columns.map((col) => ({ ...col, datakey: col.field })),

            body: data
        })
        doc.output('dataurlnewwindow'); 
    }


    return (
        <div className="App">
            <h1 align="center">React-App</h1>
            <h4 align='center'>Export Data to Pdf in Material Table</h4>
            <button onClick={downloadPdf}>Exportar</button>

        </div>
    );
}

export default ShowData;
